
public class lab02_HomeE5_pyramid {
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=0;i<=4;i++)
		{
			//System.out.print(" LINE1");
			for(int j=0;j<=4-j;j++){
				System.out.print(" ");
				//System.out.print(" LINE2");
				for (int k = 0;k<j-k;k++){
					System.out.print(" *");
					//System.out.print(" LINE3");}
					System.out.println();
				}
			}
		}
	}
}
